package com.hi5adii.demos;import java.util.ArrayList;import java.util.Date;import java.util.List;public abstract class Account{     String accountNumber;     String accountHolderName;     double balance;     public List<Transaction> transactionHistory;    public Account(String accountNumber, String accountHolderName, double balance)    {        this.accountNumber = accountNumber;        this.accountHolderName = accountHolderName;        this.balance = balance;        this.transactionHistory = new ArrayList<>();    }    public void displayBalance()    {        System.out.println("Account Balance: Rs. " + balance);    }    public void deposit(double amount) throws IllegalArgumentException {        if (amount < 0) {            throw new IllegalArgumentException("Deposit amount cannot be negative.");        }        balance += amount;        System.out.println("Amount deposited successfully.");    }    public void withdraw(double amount) throws IllegalArgumentException {        if (amount < 0) {            throw new IllegalArgumentException("Withdrawal amount cannot be negative.");        }        if (amount > balance) {            throw new IllegalArgumentException("Insufficient funds.");        }        balance -= amount;        System.out.println("Amount withdrawn successfully.");    }    public abstract void calculateInterest();    public void printAccountInfo()    {        System.out.println("Account Number: " + accountNumber);        System.out.println("Account Holder: " + accountHolderName);        displayBalance();    }    public void recordTransaction(String type, double amount) {        Transaction transaction = new Transaction(new Date(), type, amount);        transactionHistory.add(transaction);    }    public void viewTransactions() {        for (Transaction transaction : transactionHistory) {            System.out.println("Date: " + transaction.getDate() + ", Type: " + transaction.getType() + ", Amount: " + transaction.getAmount());        }    }}