package com.hi5adii.demos;public class SalaryAccount extends Account{    public final double minimumBalance = 10000;    boolean active;    public double interestRate = 0.02;    public SalaryAccount(String accountNumber, String accountHolderName, double balance) {        super(accountNumber, accountHolderName, balance);        this.active = true;    }    @Override    public void withdraw(double amount) {        if (active) {            super.withdraw(amount);        } else {            System.out.println("Account frozen. No transactions allowed.");        }    }    @Override    public void calculateInterest() {        double interest = balance * interestRate;        balance += interest;        System.out.println("Interest calculated. After Interest Balance Will be: Rs. " + balance);    }    public void checkActivityOfSalaryAccount()    {        if (balance <= minimumBalance)        {            active = false;            System.out.println("Account frozen. No transactions for two months.");        }    }}