package com.hi5adii.demos;public class SavingAccount extends Account{    public final double minimumBalance = 10000;    public double interestRate = 0.03;    public SavingAccount(String accountNumber, String accountHolderName, double balance) {        super(accountNumber, accountHolderName, balance);    }    @Override    public void withdraw(double amount) {        if (balance - amount >= minimumBalance) {            super.withdraw(amount);        } else {            System.out.println("Withdrawal not allowed. Minimum balance should be maintained.");        }    }    @Override    public void calculateInterest() {        double interest = balance * interestRate;        balance += interest;        System.out.println("Interest calculated. After Interest Balance Will be: Rs. " + balance);    }}