package com.hi5adii.demos;import java.util.Date;public class Transaction{    public Date date;    public String type;    public double amount;    public Transaction(Date date, String type, double amount) {        this.date = date;        this.type = type;        this.amount = amount;    }    public Date getDate() {        return date;    }    public void setDate(Date date) {        this.date = date;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public double getAmount() {        return amount;    }    public void setAmount(double amount) {        this.amount = amount;    }    public static void performTransaction(Account account, int choice, double amount) {        try {            switch (choice)            {                case 1:                    account.deposit(amount);                    break;                case 2:                    account.withdraw(amount);                    break;                case 3:                    account.calculateInterest();                    break;                default:                    System.out.println("Invalid choice.");            }        } catch (IllegalArgumentException e)        {            System.out.println("Error: " + e.getMessage());        }    }    public static String getTransactionTypeString(int choice) {        switch (choice) {            case 1:                return "Deposit";            case 2:                return "Withdraw";            case 3:                return "Calculate Interest";            default:                return "Unknown";        }    }    public static void transferFunds(Account sourceAccount, Account destinationAccount, double amount) {        try {            sourceAccount.withdraw(amount);            destinationAccount.deposit(amount);            System.out.println("Fund transfer successful.");        } catch (IllegalArgumentException e) {            System.out.println("Error: " + e.getMessage());        }    }    public static void displayAllAccountsInformation(Account[] accounts, int accountCount)    {        System.out.println("\n**** All Accounts Information ****");        for (int i = 0; i < accountCount; i++) {            accounts[i].printAccountInfo();            System.out.println("-----------------------------");        }    }}